                        y -= pos.top;

                        if (self._mouseX == x && self._mouseY == y)
                            return;

                        if (self._ttEl) {
                            if (self._ttEl.gidx == gidx &&
                                self._ttEl.sidx == sidx &&
                                self._ttEl.iidx == iidx)
                                return;
                        }

