                var g = this.seriesGroups[groupIndex];
                var s = g.series[seriesIndex];
                var symbolType;
                if (s.symbolType != undefined)
                    symbolType = s.symbolType;
                if (symbolType == undefined)
                    symbolType = g.symbolType;

