                                if (dayOfWeek == 0) dayOfWeek = 6;
                                else dayOfWeek--;

                                var dayOfWeekType = monthlyPattern.repeatDayOfWeekType.jqxDropDownList('selectedIndex');
                                // 0 - first
                                // 1 - second
                                // 2 - third
                                // 3 - fourth
